import React from 'react';
import s from './Comments.module.css';

type Props = {
  commentName: string;
  handleClick: any;
  //id: string;
  color: string;
};

export default function Comments({ commentName, color, handleClick }: Props) {
  return (
    <li className={s.comment}>
      <div
        style={{
          backgroundColor: color,
          width: '100px',
          height: '100px',
        }}
        onClick={handleClick}
      ></div>
      {commentName}
    </li>
  );
}
