import React, { useEffect, useState } from 'react';
import s from './CommentsList.module.css';
import useLocalState from '../../hooks';

export default function CommentsList() {
  const [items, setItems] = useLocalState('items', []);

  const [activeItem, setActiveItem] = useLocalState('activeItem', []);
  const [name, setName] = useState('');

  const interval = setInterval(() => {
    const myitem = window.localStorage.getItem('items');
    if (myitem) {
      clearInterval(interval);
    }
  }, 1000);

  console.log(myitem);

  const handleChange = (event: { target: any }) => {
    setName(event.target.value);
  };

  const handleSubmit = (event: { target: any; preventDefault: () => void }) => {
    event.preventDefault();

    const updatedItems = items.map((i: { comments: any; id: any }) => {
      if (i.id == activeItem[0].id) {
        i.comments.push(name);
      }
      return i;
    });
    setItems(updatedItems);
    setName('');
  };
  //console.log('items from comments list:  ', items);
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className={s.searchForm}>
          <h3>Comments</h3>
          <input
            placeholder="Type comment here..."
            className={s.name}
            type="text"
            name="name"
            title="Place for comments"
            required
            value={name}
            onChange={handleChange}
          />

          <button type="submit" className={s.ripple}>
            Add new
          </button>
        </div>
      </form>
    </div>
  );
}
